#include "util.h"

int all_different(const char *buf, int num)
{
	unsigned mask = 0;
	for (int i = 0; i < num; ++i)
	{
		int bit = (int)(buf[i] - 'a');
		if ((mask & (1 << bit)) != 0)
		{
			return 0;
		}
		mask |= (1 << bit);
	}
	return 1;
}

int first_n_marker(const char *data, int n)
{
	char *rbuf = (char *)malloc(n);

	int result = -1;
	const char *curr = data;
	while (*curr)
	{
		int index = (int)(curr - data);
		rbuf[index % n] = *curr;

		if (index >= n && all_different(rbuf, n))
		{
			result = index + 1;
			break;
		}
		curr++;
	}

	free(rbuf);

	return result;
}

int first_start_of_packet_marker(const char *data)
{
	return first_n_marker(data, 4);
}

int first_start_of_message_marker(const char *data)
{
	return first_n_marker(data, 14);
}

int main(int argc, char *argv[])
{
	const char *data = "";

	printf("%d %d\n", first_n_marker(data, 4), first_n_marker(data, 14));

	return 0;
}
